skill_desc_str[8154] = {strName = _t("白羊座：穆先生的神工锤"), StrAttacktype = _t("buff"), StrCompDesc = _t("\t^c0c0ff[鬼斧神工]^FFFF00\r神工锤降临，对主人的目标发起攻击，将其变成石块5秒(仅对玩家生效)，无法移动无法释放技能，攻击目标及其3米范围内的敌人\r造成伤害%d%%\r^c0c0ff[谦卑之光]^FFFF00\r神工锤降临，开始在主人头顶敲击，每次敲击，使主人及队友恢复3%%最大生命值和30点体力值。最多生效5次。并使主人受到的伤害降低80%%，被攻击10次后状态消失，最多持续12秒。触发时附带解除控制效果（被守护星攻击也消耗次数）\t神器效果触发概率与神力值有关\r"), StrUseDesc = _t("\t^c0c0ff[鬼斧神工]^FFFF00\r神工锤降临，对主人的目标发起攻击，将其变成石块5秒(仅对玩家生效)，无法移动无法释放技能，攻击目标及其3米范围内的敌人\r造成伤害%d%%\r^c0c0ff[谦卑之光]^FFFF00\r神工锤降临，开始在主人头顶敲击，每次敲击，使主人及队友恢复3%%最大生命值和30点体力值。最多生效5次。并使主人受到的伤害降低80%%，被攻击10次后状态消失，最多持续12秒。触发时附带解除控制效果（被守护星攻击也消耗次数）\t神器效果触发概率与神力值有关\r"), StrDescNum = "(1.2+(player.level-1)*0.00558)*2*100", StrFuncdesc = _t("使用后获得白羊座的祝福，战斗中有概率触发^c0c0ff[鬼斧神工]^A2FFFF或^c0c0ff[谦卑之光]^A2FFFF")}
